function sumar(){var e=parseFloat(document.getElementById("num1").value),t=parseFloat(document.getElementById("num2").value);if(validar(e,t)){var n=e+t,o="La suma es: "+n;document.getElementById("resultado1").innerHTML=o,document.getElementById("resultado1").style.backgroundColor="yellow"}else document.getElementById("resultado1").innerHTML="Formato incorrecto de números"}function resta(){var e=parseFloat(document.getElementById("num1").value),t=parseFloat(document.getElementById("num2").value);if(validar(e,t)){var n=e-t,o="La resta es: "+n;document.getElementById("resultado1").innerHTML=o,document.getElementById("resultado1").style.backgroundColor="yellow"}else document.getElementById("resultado1").innerHTML="Formato incorrecto de números"}function multiplicar(){var e=parseFloat(document.getElementById("num1").value),t=parseFloat(document.getElementById("num2").value);if(validar(e,t)){var n=e*t,o="La multiplicar es: "+n;document.getElementById("resultado1").innerHTML=o,document.getElementById("resultado1").style.backgroundColor="yellow"}else document.getElementById("resultado1").innerHTML="Formato incorrecto de números"}function division(){var e=parseFloat(document.getElementById("num1").value),t=parseFloat(document.getElementById("num2").value);if(validar(e,t)){var n=e/t;if(n==1/0)alert("División entre cero"),document.getElementById("resultado").value="";else{var o="La división es: "+n;document.getElementById("resultado1").innerHTML=o,document.getElementById("resultado1").style.backgroundColor="yellow"}}else document.getElementById("resultado1").innerHTML="Formato incorrecto de números"}function validar(e,t){return isNaN(e)||isNaN(t)?!1:!0}function hacerTabla(){var e="",t=parseFloat(document.getElementById("num").value);if(0==isNaN(t)){e+="TABLA DEL "+t+"<br>";for(var n=1;10>=n;n++)e+=t+" x "+n+" = "+t*n+"<br>";document.getElementById("resultado2").innerHTML=e}else alert("Formato Incorrecto de Número")}