function calcularSubtotales(){var e=parseInt(document.getElementById("cantidad1").value),t=parseInt(document.getElementById("precio1").value),a=t*e;document.getElementById("subtotal1").value=a;var n=parseInt(document.getElementById("cantidad2").value),l=parseInt(document.getElementById("precio2").value),u=l*n;document.getElementById("subtotal2").value=u;var d=parseInt(document.getElementById("cantidad3").value),m=parseInt(document.getElementById("precio3").value),o=m*d;document.getElementById("subtotal3").value=o;var c=parseInt(document.getElementById("cantidad4").value),I=parseInt(document.getElementById("precio4").value),r=I*c;document.getElementById("subtotal4").value=r}function calcularTotal(){var e=parseInt(document.getElementById("subtotal1").value),t=parseInt(document.getElementById("subtotal2").value),a=parseInt(document.getElementById("subtotal3").value),n=parseInt(document.getElementById("subtotal4").value),l=e+t+a+n;document.getElementById("totalCompra").value=l}